            a_query.{{ column_to_compare }} = b_query.{{ column_to_compare }} and 
                a_query.{{ primary_key }} is not null and b_query.{{ primary_key }} is not null,
            (a_query.{{ column_to_compare }} is null and b_query.{{ column_to_compare }} is null),
            false
